# -*- coding: utf-8 -*-from flask import Flask, request, jsonify, send_from_directoryimport osfrom datetime import datetimeapp = Flask(__name__)# Carpeta donde se guardarán las imágenesUPLOAD_FOLDER = os.path.join(os.getcwd(), "uploads")os.makedirs(UPLOAD_FOLDER, exist_ok=True)  # Crea el directorio si no existe@app.route('/upload', methods=['POST'])def upload_image():    # Verifica si hay un archivo en la petición    if 'image' not in request.files:        return jsonify({"error": "No image provided"}), 400    file = request.files['image']    # Verifica que el archivo tenga un nombre    if file.filename == '':        return jsonify({"error": "No selected image"}), 400    # Asegura que el archivo es de tipo imagen    if file and file.filename.lower().endswith(('png', 'jpg', 'jpeg')):        # Crea un nombre de archivo único usando la fecha y hora actuales        filename = f"uploaded_image_{datetime.now().strftime('%Y%m%d%H%M%S')}.jpg"        file_path = os.path.join(UPLOAD_FOLDER, filename)        # Guarda la imagen en la carpeta especificada        file.save(file_path)        # Retorna una URL para acceder a la imagen        image_url = f"http://10.0.2.2:5000/uploads/{filename}"  # Cambia la URL a HTTPS        return jsonify({"location": image_url}), 200    else:        return jsonify({"error": "Invalid file type"}), 400# Ruta para servir las imágenes guardadas@app.route('/uploads/<filename>')def get_image(filename):    return send_from_directory(UPLOAD_FOLDER, filename)if __name__ == '__main__':    app.run(debug=True, host='0.0.0.0', port=5000)  # Sin SSL para pruebas locales